def from_acid_to_color(myarray):
    for i in range(0, len(myarray), 1):
        output = myarray
        if myarray[i] == 'A':
            output[i] = 'black'
        elif myarray[i] == 'C':
            output[i] = 'grey'
        elif myarray[i] == 'D' :
            output[i] = 'firebrick'
        elif myarray[i] == 'E' :
            output[i] = 'red'
        elif myarray[i] == 'F' :
            output[i] == 'sienna'
        elif myarray[i] == 'G' :
            output[i] = 'tan'
        elif myarray[i] == 'H' :
            output[i] = 'gold'
        elif myarray[i] == 'I' :
            output[i] = 'darkkhaki'
        elif myarray[i] == 'K' :
            output[i] = 'olivedrab'
        elif myarray[i] == 'L': 
            output[i] = 'chartreuse'
        elif myarray[i] == 'M' :
            output[i] = 'darkgreen'
        elif myarray[i] == 'N' :
            output[i] = 'mediumspringgreen'
        elif myarray[i] == 'P' :
            output[i] = 'darkcyan'
        elif myarray[i] == 'Q' :
            output[i] = 'deepskyblue'
        elif myarray[i] == 'R' :
            output[i] = 'slategrey'
        elif myarray[i] == 'S' :
            output[i] = 'royalblue'
        elif myarray[i] == 'T' :
            output[i] = 'navy'
        elif myarray[i] == 'V' :
            output[i] = 'blue'
        elif myarray[i] == 'W' :
            output[i] = 'mediumpurple'
        elif myarray[i] == 'Y' :
            output[i] = 'darkchid'

    return myarray

